```JavaScript
/**
 * @param {number[][]} grid
 * @return {number}
 */
const largestIsland = function (grid) {
  let count = 2;
  let max = maxAreaOfIsland(grid, count++);

  for (let i = 0; i < grid.length; i++) {
    for (let j = 0; j < grid[i].length; j++) {
      // 
      if (grid[i][j] !== 0) {
        continue;
      }

      //  1
      if (tryDFS(grid, i, j) === 0) {
        max = max < 1 ? 1 : max;
        continue;
      }

      //  -> 
      const origin = grid[i][j];
      grid[i][j] = 1;
      const cur = maxAreaOfIsland(grid, count++);
      max = max < cur ? cur : max;
      grid[i][j] = origin;
    }
  }
  return max;
};

/**
 * @param {number[][]} grid
 * @param {number} count
 * @return {number}
 */
var maxAreaOfIsland = function (grid, count) {
  let max = 0;
  for (let i = 0; i < grid.length; i++) {
    for (let j = 0; j < grid[i].length; j++) {
      if (grid[i][j] === 1) {
        max = Math.max(max, dfs(grid, i, j, count));
      }
    }
  }

  return max;
};

/**
 * @param {number[][]} grid
 * @param {number} i
 * @param {number} j
 * @param {number} count
 * @return {number}
 */
const dfs = (grid, i, j, count) => {
  if (i < 0 || i >= grid.length || j < 0 || j >= grid[0].length) return 0;
  if (grid[i][j] === 0 || grid[i][j] === count) return 0;
  grid[i][j] = count;
  return 1
    + dfs(grid, i - 1, j, count)
    + dfs(grid, i + 1, j, count)
    + dfs(grid, i, j - 1, count)
    + dfs(grid, i, j + 1, count);
}

/**
 * 
 * @param {number[][]} grid
 * @param {number} i
 * @param {number} j
 * @return {number}
 */
const tryDFS = (grid, i, j) => {
  return (i >= 1 ? (grid[i - 1][j]) : 0)
    + (i < grid.length - 1 ? grid[i + 1][j] : 0)
    + (j >= 1 ? grid[i][j - 1] : 0)
    + (j < grid[i].length - 1 ? grid[i][j + 1] : 0);
}
```

